sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;
	console.log(c.data.vAccounts);
	 var getEmbedToken = "https://mindshiftpowerbi.azurewebsites.net/api/PowerBIHttpTrigger?code=QvgqsUVSBrxTu68P5inNO/GK1LjOQ3J4gcUamvsgRETS8ZG3ModOzA==";  

	 var  companyFilter ={};

	  companyFilter = {	$schema: "http://powerbi.com/product/schema#basic",
				target: {
				table: "tbl_SNOW_Incidents",
				column: "company_id"
								},
				 operator: "In",
					values: c.data.vAccounts
				}


        $.ajax({  
            url: getEmbedToken,  
            jsonpCallback: 'callback',  
            contentType: 'application/javascript',  
            dataType: "jsonp",  
            success: function (json) {  

                var models = window['powerbi-client'].models;  

                var embedConfiguration = {  
                    type: 'report',  
                    id: json.ReportId,  
                    embedUrl: json.EmbedUrl,  
                    filters:[companyFilter],
                    tokenType: models.TokenType.Embed,  
                    accessToken: json.EmbedToken  ,
			settings:{
					filterPaneEnabled:false
					}
                };  

                var $reportContainer = $('#reportContainer');  
                var report = powerbi.embed($reportContainer.get(0), embedConfiguration);  


            },  
            error: function () {  
                alert("Error");  
            }  
        });  

}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	VisibleAccounts();


	function VisibleAccounts()
	{
		var vAccounts = [];

		var gr = new GlideRecord("core_company");
		gr.addEncodedQuery("sys_domain!=global^ORsys_domain=NULL");
	  gr.addEncodedQuery("active=true");

		gr.query();		
		if(!gs.getUser().hasRole('itil')){
			while(gr.next()){
				vAccounts.push(gr.getDisplayValue("sys_id"));
			}
		}
		else
			{
				vAccounts.push(gs.getUser().getCompanyID());
			}
		data.vAccounts = vAccounts;
		console.log(vAccounts);
		return gr.getRowCount();
	}


})();
